mapper.select:
select d.deptname, m.membername, m.rank, m.memberno, d.deptnum from member m left join dept d on m.deptnum = d.deptnum where d.deptnum = #{deptNum}
and m.memberno = #{keyword}
and m.membername LIKE '%' || #{keyword} || '%'

mapper.update:
UPDATE member
SET memberName = #{memberName}
	, address = #{address}
	, email = #{email}
	, rank = #{rank}
	, phone = #{phone}
WHERE memberNo = #{memberNo}
