mapper.update:
UPDATE community
SET chit = chit + 1
WHERE cno = #{cno}

UPDATE community
SET ctitle = #{ctitle}
	, ccontent = #{ccontent}
WHERE cno = #{cno}

mapper.select:
SELECT count(*)
FROM clike
WHERE cno = #{cno }
	AND memberno = #{memberno }

SELECT count(*)
FROM clike
WHERE memberno = #{memberno }

mapper.delete:
DELETE clike
WHERE memberno = #{memberno }
	AND cno = #{cno }

mapper.select:
SELECT count(*) 
FROM creply
WHERE cno = #{cno}

mapper.insert:
        INSERT INTO creply(
			cono, cno, memberno, cocontent )

		VALUES ( creply_seq.nextval, #{cno },  #{memberno }
				, #{cocontent }
		)

mapper.select:
SELECT cono, cno, memberno, cocontent
FROM creply 
WHERE cono = #{cono}

mapper.update:
UPDATE creply
SET cocontent = #{cocontent}
  , cocreatedate = CURRENT_TIMESTAMP()
WHERE cono = #{cono} and memberno = #{memberno}
