mapper.select:
		SELECT * FROM (
			SELECT rownum rnum, L.* FROM (
				SELECT
					*
				FROM leave A JOIN leaveDetail D
                ON A.leaveNo =  D.leaveNo
                WHERE A.memberNo=#{loginId}
				ORDER BY A.leaveNo DESC
			) L
		) LEAVE
		WHERE rnum BETWEEN #{paging.startNo} AND #{paging.endNo}

		SELECT * FROM (
 			SELECT rownum rnum, L.* FROM (
				SELECT
				A.*, m.rank
				FROM leave A 
                join member m on A.memberno = m.memberno

               WHERE A.deptname = #{deptName}
				ORDER BY A.leaveNo DESC)L
                ) LEAVE
		WHERE rnum BETWEEN #{paging.startNo} AND #{paging.endNo}

mapper.update:
update leave
	set leaveconfirm = #{confirm}
	where leaveno = #{no}
