mapper.select:
SELECT scheduleno
FROM schedule
WHERE title = #{title } and startdate = #{startdate } and enddate = #{enddate }

mapper.update:
UPDATE schedule
SET
      startdate = #{startdate },
    enddate = #{enddate }
WHERE  scheduleno = #{scheduleno }

mapper.select:
SELECT scheduleno
   FROM schedule
WHERE memberno = #{memberno } AND startdate = #{startdate } AND enddate = #{enddate } AND title = #{title }
